import React, { useState } from 'react';
import './App.css';

function App() {

  const [password, setPassword] = useState('');
  const [encryptedPassword, setEncryptedPassword] = useState('');
  const [decryptedPassword, setDecryptedPassword] = useState('');

  const handleEncrypt = async () => {
    try {
      const response = await fetch('http://localhost:5000/encrypt', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }),
      });

      const data = await response.json();
      setEncryptedPassword(data.encryptedPassword);

    } catch (error) {
      console.error('Error encrypting password:', error);
    }
  };

  const handleDecrypt = async () => {
    try {
      const response = await fetch('http://localhost:5000/decrypt', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          encryptedPassword,
         iv: 'utf-8', // Replace with the actual IV received from the encryption response
         key: 'hex', // Replace with the actual key received from the encryption response
        }),
      });

      const data = await response.json();
      setDecryptedPassword(data.decryptedPassword);
    } catch (error) {
      console.error('Error decrypting password:', error);
    }
  };

  return (
    <div className="App">
    <h1>Password Encrypter</h1>
    <label>
      Enter Password:
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
    </label>
    <br />
    <button onClick={handleEncrypt}>Encrypt</button>
    <br />
    <label>
      Encrypted Password:
      <input type="text" value={encryptedPassword} readOnly />
    </label>
    <br />
    <button onClick={handleDecrypt}>Decrypt</button>
    <br />
    <label>
      Decrypted Password:
      <input type="text" value={decryptedPassword} readOnly />
    </label>
    </div>
  );
}

export default App;
