import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [password, setPassword] = useState('');
    const [encryptedPassword, setEncryptedPassword] = useState('');
    const [decryptedPassword, setDecryptedPassword] = useState('');

    const handleEncrypt = async () => {
        try {
            const response = await axios.post('http://localhost:5000/encrypt', { password });
            setEncryptedPassword(response.data.encryptedPassword);
        } catch (error) {
            console.error(error);
        }
    };

    const handleDecrypt = async () => {
        try {
            const response = await axios.post('http://localhost:5000/decrypt', {
                encryptedPassword,
                iv: 'your_iv_here', // Replace with the actual IV
                key: 'your_key_here', // Replace with the actual key
            });
            setDecryptedPassword(response.data.decryptedPassword);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h1>Password Encrypter/Decrypter</h1>
            <div>
                <label>Password: </label>
                <input
                    type="text"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button onClick={handleEncrypt}>Encrypt</button>
            </div>
            <div>
                <label>Encrypted Password: </label>
                <input type="text" value={encryptedPassword} readOnly />
            </div>
            <div>
                <label>Decrypted Password: </label>
                <input type="text" value={decryptedPassword} readOnly />
                <button onClick={handleDecrypt}>Decrypt</button>
            </div>
        </div>
    );
}

export default App;
